def find_factorial(x):
    if x <= 1:
        return 1
    else:
        return x * find_factorial(x - 1)

def compute_pmf(x, y):
    return find_factorial(x + y) / (find_factorial(x) * find_factorial(y))

def find_probabilities(x, y):
    return [
        (compute_pmf(i, x - i), i)
        for i in range(x - y + 1, x + 1)
    ]

