from sympy.parsing.sympy_parser import parse_expr

def get_nonzero_expressions(expressions):
    return [expression for expression in expressions if Nonzero(expression)]

def is_nonzero_expression(expression):
    return Nonzero(expression)

# The symbols in sympy should not be used as function names, for example, math.sin cannot be used as symbol.sin
# The reason is that the function name math.sin conflicts with the symbol name sin
def sympy_function_name_of_symbol(symbol):
    symbol_name = symbol.name
    return'math.'+symbol_name

def sympy_expr_is_function(expr):
    return isinstance(expr, (Symbol, parse_expr))

def sympy_expr_is_symbol(expr):
    return isinstance(expr, Symbol)

# Note: In sympy 1 is equal to True
def sympy_expr_is_one(expr):
    return expr == True

def sympy_expr_is_zero(expr):
    return expr == False

def sympy_expr_is_add(expr):
    return isinstance(expr, sum)

def sympy_expr_is_minus(expr):
