import math

def _calc_confidence(keywords, answer, all_possible_answers):
    """
    Returns a confidence score for a candidate answer to a reasoning problem.
    Arguments:
    keywords - list of keywords that might appear in the reasoning
    answer - the candidate answer
    all_possible_answers - list of possible answers
    """
    confidence = 0.0
    possible_keywords = []
    for keyword in keywords:
        if keyword.isdigit() or keyword == ':':
            continue
        if keyword not in possible_keywords:
            possible_keywords.append(keyword)

    for possible_answer in all_possible_answers:
        found = True
        for possible_keyword in possible_keywords:
            if possible_keyword in answer and possible_keyword not in possible_answer:
                found = False
                break
        if found:
            confidence += 1
            break

    return round(100 * confidence / len(possible_keywords), 2)
