def calc_entropy(arr):
    '''Return the entropy of arr'''
    arr = Counter(arr)
    s = 0
    for i in arr:
        p = arr[i] / len(arr)
        s -= p * log(p, 2)
    return s

def entropy_of_prob_arr(prob_arr):
    '''Return the entropy of the array of probabilities'''
    arr = []
    for i in prob_arr:
        arr.extend([i] * len(prob_arr))
    return calc_entropy(arr)

def entropy_of_prob_dict(prob_dict):
    '''Return the entropy of the dictionary of probabilities'''
    arr = []
    for k in prob_dict:
        arr.extend([prob_dict[k]] * len(prob_dict[k]))
    return calc_entropy(arr)

def calc_banman_score(arr, banman_dict):
    '''Return the banman score for arr'''
    score = 0
    for i in arr:
        score -= log(banman_dict[i] / (1 - banman_dict[i]), 2)
    return score
